#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
}
$ret=0
if (Test-Path "$basedir/-S$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/-S$exe" NODE_OPTIONS='--experimental-loader="toast/src/loader.mjs"' node "$basedir/../toast/toast-cli.mjs" $args
  } else {
    & "$basedir/-S$exe" NODE_OPTIONS='--experimental-loader="toast/src/loader.mjs"' node "$basedir/../toast/toast-cli.mjs" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "-S$exe" NODE_OPTIONS='--experimental-loader="toast/src/loader.mjs"' node "$basedir/../toast/toast-cli.mjs" $args
  } else {
    & "-S$exe" NODE_OPTIONS='--experimental-loader="toast/src/loader.mjs"' node "$basedir/../toast/toast-cli.mjs" $args
  }
  $ret=$LASTEXITCODE
}
exit $ret
